mapper.update:
UPDATE FREEBOARD 
SET TITLE = #{title},
    CONTENT = #{content}
WHERE ID = #{id}

mapper.select:
select COUNT(*)  
from FREEBOARD_VIEW 
where user_email = #{email} 
and freeboard_id = #{id}
and extract(minute FROM NOW()-CREATED_AT) <= 5

mapper.update:
UPDATE FREEBOARD 
SET VIEWS = VIEWS + 1
WHERE ID = #{id}
